INSERT INTO `chkup_lab_meaning` (`lab_meaning_id`, `lab_group_id`, `lab_name`, `normal_min`, `normal_max`, `normal_male_min`, `normal_male_max`, `normal_female_min`, `normal_female_max`, `unit`, `low_result`, `high_result`, `abnormal_result`) VALUES
(1, 1, 'ปริมาณฮีโมโกลบิน (Hb)', 0, 0, 13, 15, 12, 15, 'g / dl', 'ปริมาณฮีโมโกลบินต่ำกว่าปกติ: แสดงถึงภาวะโลหิตจาง เกิดได้จากหลายสาเหตุ เช่น โรคเลือดทางพันธุกรรม ภาวะเสียเลือด ภาวะขาดธาตุเหล็กหรือสารอาหาร รวมถึงโรคเรื้อรัง หากมีอาการผิดปกติแนะนำตรวจเพิ่มเติมและติดตามกับแพทย์ผู้เชี่ยวชาญ', 'ปริมาณฮีโมโกลบินสูงกว่าปกติ: พบได้ในภาวะขาดสารน้ำในร่างกาย โรคเลือดข้นจากภาวะขาดออกซิเจนเรื้อรัง รวมถึงโรคไขกระดูกผิดปกติ  หากมีอาการผิดปกติแนะนำตรวจเพิ่มเติมและติดตามกับแพทย์ผู้เชี่ยวชาญ', ''),
(2, 1, 'ความเข้มข้นของเม็ดเลือดแดง (Hct)', 0, 0, 40, 50, 36, 45, '%', 'ความเข้มข้นของเม็ดเลือดแดงต่ำกว่าปกติ: แสดงถึงภาวะโลหิตจาง เกิดได้จากหลายสาเหตุ เช่น โรคเลือดทางพันธุกรรม ภาวะเสียเลือด ภาวะขาดธาตุเหล็กหรือสารอาหาร รวมถึงภาวะโรคเรื้อรังที่ทำให้มีโลหิตจาง', 'ความเข้มข้นของเม็ดเลือดแดงสูงกว่าปกติ: พบได้ในภาวะขาดสารน้ำในร่างกาย โรคเลือดข้นจากภาวะขาดออกซิเจนเรื้อรัง รวมถึงโรคไขกระดูกผิดปกติ', ''),
(3, 1, 'ปริมาณเม็ดเลือดขาว (WBC)', 5000, 10000, 0, 0, 0, 0, 'cells/cu.mm.', 'ปริมาณเม็ดเลือดขาวต่ำกว่าปกติ: สาเหตุอาจเกิดจากยาบางชนิด การฉายรังสี การติดเชื้อไวรัส และโรคของไขกระดูก แนะนำตรวจติดตามซ้ำที่ 4-6 สัปดาห์ หรือพบแพทย์ผู้เชี่ยวชาญ', 'ปริมาณเม็ดเลือดขาวสูงกว่าปกติ: แสดงถึงภาวะการติดเชื้อในร่างกายโดยเฉพาะแบคทีเรีย การอักเสบในร่างกาย โรคของไขกระดูกรวมถึงมะเร็งเม็ดเลือดขาว แนะนำตรวจติดตามซ้ำที่ 4-6 สัปดาห์ หรือพบแพทย์ผู้เชี่ยวชาญ', ''),
(4, 1, 'Neutrophil', 40, 74, 0, 0, 0, 0, '%', 'เม็ดเลือดขาวชนิด Neutrophil ปริมาณต่ำพบได้ในการติดเชื้อไวรัส ยาบางชนิด หรือภาวะภูมิคุ้มกันต่ำ', 'เม็ดเลือดขาวชนิด Neutrophil ปริมาณสูงอาจเกิดการอักเสบและติดเชื้อเฉียบพลันในร่างกายโดยเฉพาะการติดเชื้อแบคทีเรีย ', ''),
(5, 1, 'Lymphocyte', 19, 48, 0, 0, 0, 0, '%', '', 'เม็ดเลือดขาวชนิด Lymphocyte ปริมาณสูงขึ้นในภาวะที่มีการติดเชื้อไวรัส และมะเร็งเม็ดเลือดขาวบางชนิด', ''),
(6, 1, 'Monocyte', 3, 9, 0, 0, 0, 0, '%', '', 'เม็ดเลือดขาวชนิด Monocyte อาจพบสูงในผู้ที่มีการติดเชื้อไวรัส วัณโรค เชื้อรา และมะเร็งเม็ดเลือดขาวบางชนิด', ''),
(7, 1, 'Eosinophil', 0, 7, 0, 0, 0, 0, '%', '', 'เม็ดเลือดขาวชนิด Eosinophil ปริมาณสูงในผู้ที่เป็นภูมิแพ้/หอบหืด การติดเชื้อพยาธิ', ''),
(8, 1, 'Basophil', 0, 2, 0, 0, 0, 0, '%', '', 'เม็ดเลือดขาวชนิด Basophil ปริมาณสูงขึ้นในภาวะที่มีอาการภูมิแพ้', ''),
(9, 1, 'เม็ดเลือดแดง (RBC)', 4, 5.5, 0, 0, 0, 0, 'x1000000 cells/cu.mm', 'ปริมาณเม็ดเลือดแดงต่ำกว่าปกติ: เกิดได้จากหลายสาเหตุ เช่น โรคเลือดทางพันธุกรรม ภาวะเสียเลือด ภาวะขาดธาตุเหล็กหรือสารอาหาร รวมถึงภาวะโรคเรื้อรังที่ทำให้มีโลหิตจาง', 'ปริมาณเม็ดเลือดแดงสูงกว่าปกติ: พบได้ในภาวะขาดสารน้ำในร่างกาย โรคเลือดข้นจากภาวะขาดออกซิเจนเรื้อรัง รวมถึงโรคไขกระดูกผิดปกติ', ''),
(10, 1, 'การกระจายขนาดของเม็ดเลือดแดง (RDW)', 11.5, 14.5, 0, 0, 0, 0, '%', 'RDW ต่ำ: หมายถึงมีเม็ดเลือดแดงที่ขนาดต่างกันไม่ถึง 11.5%', 'RDW สูง: เกิดจากขนาดเม็ดเลือดแดงต่างกันมาก พบในภาวะที่ขาดธาตุเหล็ก ขาดวิตามิน B12 หรือกรดโฟลิก', ''),
(11, 1, 'ความเข้มข้นเฉลี่ยของฮีโมโกลบินในเม็ดเลือดแดง (MCHC)', 33, 37, 0, 0, 0, 0, 'g/dL', 'ความเข้มข้นเฉลี่ยของฮีโมโกลบินในเม็ดเลือดแดงต่ำกว่าปกติ: สัมพันธ์กับระดับฮีโมโกลบินและขนาดเม็ดเลือดแดงที่ลดลง พบได้ในภาวะโลหิตจางจากขาดธาตุเหล็ก โรคธาลัสซีเมีย', 'ความเข้มข้นเฉลี่ยของฮีโมโกลบินในเม็ดเลือดแดงสูงกว่าปกติ: พบในภาวะโลหิตจางจากเม็ดเลือดแดงแตกง่าย โรคความผิดปกติของเม็ดเลือดแดงทางพันธุกรรม', ''),
(12, 1, 'ปริมาณเฉลี่ยของฮีโมโกลบินในเม็ดเลือดแดง (MCH)', 27, 31, 0, 0, 0, 0, 'pg.', 'ปริมาณเฉลี่ยของฮีโมโกลบินในเม็ดเลือดแดงต่ำกว่าปกติ: พบในภาวะโลหิตจางที่เม็ดเลือดแดงเล็กกว่าปกติ', 'ปริมาณเฉลี่ยของฮีโมโกลบินในเม็ดเลือดแดงสูงกว่าปกติ: พบในภาวะโลหิตจางที่เม็ดเลือดแดงใหญ่กว่าปกติ', ''),
(13, 1, 'ปริมาตรของเม็ดเลือดแดงโดยเฉลี่ย (MCV)', 80, 99, 0, 0, 0, 0, 'fl.', 'ปริมาตรของเม็ดเลือดแดงโดยเฉลี่ยต่ำกว่าปกติ: พบในภาวะโลหิตจางที่เม็ดเลือดแดงเล็กกว่าปกติ เช่น ภาวะที่ร่างกายขาดเหล็ก โรคธาลัสซีเมีย', 'ปริมาตรของเม็ดเลือดแดงโดยเฉลี่ยสูงกว่าปกติ: พบในภาวะโลหิตจางที่เม็ดเลือดแดงใหญ่กว่าปกติ ภาวะขาดวิตามิน B12 หรือกรดโฟลิก', ''),
(14, 1, 'ปริมาณเกล็ดเลือด (Platelet)', 100000, 400000, 0, 0, 0, 0, 'cells/cu.mm.', 'ปริมาณเกล็ดเลือดต่ำกว่าปกติ: เสี่ยงต่อภาวะเลือดออกง่าย สาเหตุอาจเกิดจากยา สารเคมี โรคทางภูมิคุ้มกัน ม้ามโต รวมถึงโรคไขกระดูกผิดปกติ', 'ปริมาณเกล็ดเลือดมากกว่าปกติ: พบได้ในภาวะการอักเสบเรื้อรังของร่างกาย ภาวะขาดธาตุเหล็ก และความผิดปกติของไขกระดูก', ''),
(15, 1, 'Platelet large cell ratio [P-LCR]', 0, 0, 0, 0, 0, 0, '', '', '', ''),
(16, 1, 'ค่าเฉลี่ยของปริมาตรเกล็ดเลือด (MPV)', 7.2, 11.1, 0, 0, 0, 0, 'fL.', 'MPV ต่ำ: พบในสภาวะไขกระดูกเสื่อม ซึ่งมีผลทำให้การสร้างเม็ดเลือดแดงและเกล็ดเลือดได้ขนาดที่เล็กลง', 'MPV สูง: พบในภาวะเกล็ดเลือดมีขนาดเฉลี่ยที่ใหญ่กว่าปกติ พบใน\nโรคที่มีความผิดปกติของไขกระดูก', ''),
(17, 1, 'การกระจายขนาดของเกล็ดเลือด PDW', 9.6, 15.2, 0, 0, 0, 0, '%', 'PDW ต่ำ: หมายถึงมีเกล็ดเลือดที่ขนาดต่างกันไม่ถึง 9.6%', 'PDW สูง: หมายถึงมีเกล็ดเลือดที่ขนาดต่างกันมากกว่า 15.2%', ''),
(18, 2, 'สี (Color)', 0, 0, 0, 0, 0, 0, '', '', '', 'สีปัสสาวะที่ซีด หมายถึง ปัสสาวะเจือจางมาก \nปัสสาวะสีแดง อาจเกิดจากมีเลือดออกในทางเดินปัสสาวะหรือจากยาบางชนิด\nปัสสาวะสีอื่นๆ อาจเกิดจากหลายวาเหตุ เช่น อาหาร/ยาที่รับประทาน'),
(19, 2, 'ความใส (Appearance)', 0, 0, 0, 0, 0, 0, '', '', '', 'ปัสสาวะที่มีลักษณะขุ่นเกิดจากตะกอนในปัสสาวะ โดยความขุ่นขึ้นกับปริมาณสารที่พบ'),
(20, 2, 'ความเป็นกรดด่าง(pH)', 4.6, 8, 0, 0, 0, 0, '', 'ปัสสาวะมีค่า PH<4.6: ปัสสาวะเป็นกรด พบได้ในผู้ป่วยโรคไต ผู้ที่มีภาวะขาดสารน้ำ อดอาหารเป็นเวลานาน และการติดเชื้อทางเดินปัสสาวะจากแบคทีเรียบางกลุ่ม ', 'ปัสสาวะมีค่า PH>8.0: ปัสสาวะเป็นด่าง พบในผู้ที่มีการติดเชื้อระบบทางเดินปัสสาวะจากแบคทีเรียบางกลุ่ม โรคไตเรื้อรัง ยา หรือปัสสาวะตั้งทิ้งไว้นานก่อนการตรวจ', ''),
(21, 2, 'ความถ่วงจำเพาะ (Specific gravity)', 1.003, 1.03, 0, 0, 0, 0, '', 'ปัสสาวะมีค่าความถ่วงจำเพาะต่ำ: เกิดจากไตไม่สามารถทำให้ปัสสาวะเข้มข้นได้ อาจเกิดจากการดื่มน้ำมากเกิน และผู้ป่วยโรคเบาจืด', 'ปัสสาวะมีค่าความถ่วงจำเพาะสูง: สามารถพบได้ในภาวะขาดน้ำ', ''),
(22, 2, 'น้ำตาลในปัสสาะ (Glucose)', 0, 0, 0, 0, 0, 0, '', '', '', 'พบในผู้ป่วยเบาหวาน หรือการทานยาเบาหวานบางชนิด'),
(23, 2, 'โปรตีนในปัสสาวะ (Protein)', 0, 0, 0, 0, 0, 0, '', '', '', 'อาจพบได้ในภาวะปกติ เช่น หลังการออกกำลังกายอย่างหนัก ภาวะที่นั่งหรือยืนนานๆ  ภาวะตั้งครรภ์ และผู้ป่วยโรคไต'),
(24, 2, 'เลือด (Blood)', 0, 0, 0, 0, 0, 0, '', '', '', 'พบได้ในภาวะที่มีการแตกทำลายของกล้ามเนื้อ เช่น การออกกำลังกายหนัก และในผู้ป่วยโรคไต'),
(25, 2, 'สารคีโตนในปัสสาวะ (Ketone)', 0, 0, 0, 0, 0, 0, '', '', '', 'พบในผู้ป่วยเบาหวานที่มีภาวะเลือดเป็นกรดร่วมด้วย นอกจากนี้พบได้ในผู้ที่มีอาการชัก อาเจียน ท้องเสีย อดอาหาร'),
(26, 2, 'น้ำดี (Bilirubin)', 0, 0, 0, 0, 0, 0, '', '', '', 'พบได้ในผู้ป่วยโรคตับและทางเดินน้ำดีอุดตันบางราย'),
(27, 2, 'สารยูโรบิลลิโนเจน (Urobilinogen)', 0, 0, 0, 0, 0, 0, '', '', '', 'พบได้ในผู้ป่วยโรคตับและทางเดินน้ำดีอุดตันบางราย'),
(28, 2, 'สารไนไตรท์ (Nitrite)', 0, 0, 0, 0, 0, 0, '', '', '', 'พบได้ในผู้ที่มีการติดเชื้อระบบทางเดินปัสสาวะจากแบคทีเรียบางชนิดและมักพบแบคทีเรียในปัสสาวะร่วมด้วย'),
(29, 2, 'เม็ดเลือดแดงในปัสสาวะ (RBC)', 0, 3, 0, 0, 0, 0, 'cells/HPF', '', '', 'พบได้ในผู้ที่หลอดเลือดในระบบทางเดินปัสสาวะผิดปกติ ทั้งนี้อาจเกิดจากการปนเปื้อนเลือดจากประจำเดือน หรือริดสีดวงทวาร'),
(30, 2, 'เม็ดเลือดขาวในปัสสาวะ (WBC)', 0, 5, 0, 0, 0, 0, 'cells/HPF', '', '', 'พบในผู้ที่มีภาวะอักเสบ/ติดเชื้อทางเดินปัสสาวะโดยต้องมีอาการร่วมด้วย ทั้งนี้ยังพบได้ในผู้ที่มีนิ่วทางเดินปัสสาวะ หรือในผู้ที่ใส่สายสายสวนปัสสาวะ'),
(31, 2, 'เซลล์บุผิวในปัสสาวะ (Squamous Epithelium)', 0, 5, 0, 0, 0, 0, 'cells/HPF', '', '', 'การพบเซลล์เยื่อบุผิวในปัสสาวะบ่งบอกถึงการเก็บปัสสาวะไม่ถูกวิธี อาจจะทำให้การแปลผลคลาดเคลื่อนหรือแปลผลไม่ได้  หากมีอาการผิดปกติแนะนำให้เก็บปัสสาวะส่งตรวจใหม่'),
(32, 2, '*แบคทีเรียในปัสสาวะ (Bacteria)', 0, 0, 0, 0, 0, 0, '', '', '', ''),
